mapper.select:
select m.id, m.member_name, m.member_email, m.member_phone, m.member_last_login_date, m.created_date, m.updated_date, ifnull(sp.subscription_payment_status, 'failed') as "subscription_payment_status" from tbl_member m left join tbl_subscription_payment sp on m.id = sp.member_id where m.member_status = 'active' and m.member_role = 'buyer'
and ( m.id like concat('%', #{keyword}, '%') or m.member_name like concat('%', #{keyword}, '%') )
order by m.id desc limit #{criteria.count} offset #{criteria.offset}

select id, member_email, member_name, member_birth, member_phone, member_gender, member_role, member_status, member_last_login_date, created_date, updated_date
from tbl_member
where id = #{id}

select m.id, m.member_email, m.member_name, m.member_birth, m.member_phone, m.member_provider, m.member_gender, m.member_role, m.member_status, m.member_last_login_date, m.created_date, m.updated_date, v.m_id, s_id, v.subscription_start_date, v.subscription_end_date, v.subscription_status, v.p_id, ifnull(v.subscription_payment_status, 'failed') as "subscription_payment_status" from tbl_member m left join view_subscription_sub_payment v on m.id = v.m_id where m.member_status = 'active' and m.member_role = 'buyer' and (v.subscription_status is null or v.subscription_payment_status != 'success')
and ( id like concat('%', #{keyword}, '%') or member_name like concat('%', #{keyword}, '%') )
order by m.id desc limit #{criteria.count} offset #{criteria.offset}

select m.id, m.member_email, m.member_name, m.member_birth, m.member_phone, m.member_provider, m.member_gender, m.member_role, m.member_status, m.member_last_login_date, m.created_date, m.updated_date, v.m_id, s_id, v.subscription_start_date, v.subscription_end_date, v.subscription_status, v.p_id, ifnull(v.subscription_payment_status, 'failed') as "subscription_payment_status" from tbl_member m left join view_subscription_sub_payment v on m.id = v.m_id where m.member_status = 'active' and m.member_role = 'buyer' and v.subscription_payment_status = 'success'
and ( m.id like concat('%', #{keyword}, '%') or m.member_name like concat('%', #{keyword}, '%') )
order by m.id desc limit #{criteria.count} offset #{criteria.offset}
