mapper.select:
select p.id, p.product_name, p.product_price, p.product_quantity, p.product_title_image_url, p.product_info_image_url, p.product_min_number, p.product_status, p.member_id, p.created_date, p.updated_date from tbl_product p where p.product_status = 'active'
and ( product_name like concat('%', #{keyword}, '%') )
order by p.id desc limit #{criteria.count} offset #{criteria.offset}
