mapper.select:
select id, product_name, product_price, product_quantity, product_category, product_title_image_url, product_info_image_url, product_min_number, product_status, member_id, created_date, updated_date from tbl_product where product_status = 'active'
and ( product_name like concat('%', #{keyword}, '%') )
order by id desc limit #{criteria.count} offset #{criteria.offset}
